package com.dennytech.wiiivideo.playlist.view;import android.content.Context;import android.util.AttributeSet;import android.view.View;import android.widget.LinearLayout;import android.widget.TextView;import com.dennytech.wiiivideo.R;import com.dennytech.wiiivideo.data.Playlist;import com.dennytech.wiiivideo.widget.NetworkImageView;public class PlaylistListItem extends LinearLayout {		private NetworkImageView authorIcon;	private TextView authorName;	private TextView title;	private NetworkImageView videoThumb0;	private TextView videoTitle0;	private NetworkImageView videoThumb1;	private TextView videoTitle1;	private View more;		public PlaylistListItem(Context context) {		super(context);	}	public PlaylistListItem(Context context, AttributeSet attrs) {		super(context, attrs);	}		@Override	protected void onFinishInflate() {		super.onFinishInflate();		authorIcon = (NetworkImageView) findViewById(R.id.author_icon);		authorName = (TextView) findViewById(R.id.author_name);		title = (TextView) findViewById(R.id.title);		videoThumb0 = (NetworkImageView) findViewById(R.id.video_thumb0);		videoThumb1 = (NetworkImageView) findViewById(R.id.video_thumb1);		videoTitle0 = (TextView) findViewById(R.id.video_title0);		videoTitle1 = (TextView) findViewById(R.id.video_title1);		more = (TextView) findViewById(R.id.more);	}	public void setData(Playlist data) {		authorIcon.setImage(data.authorIcon);		authorName.setText(data.authorName);		title.setText(data.title);		videoThumb0.setImage(data.videos[0].thumb);		videoThumb1.setImage(data.videos[1].thumb);		videoTitle0.setText(data.videos[0].title);		videoTitle1.setText(data.videos[1].title);	}}